Terraform Plugin Framework (Preferred)Terraform Plugin SDK V2 <pre><code>func flattenStructure(ctx context.Context, apiObject *service.Structure) (types.List, diag.Diagnostics) {
var diags diag.Diagnostics
elemType := types.ObjectType{AttrTypes: structureAttrTypes}

if apiObject == nil {
return types.ListNull(elemType), diags
}

obj := map[string]attr.Value{
// ... nested attribute handling ...
}
objVal, d := types.ObjectValue(structureAttrTypes, obj)
diags.Append(d...)

listVal, d := types.ListValue(elemType, []attr.Value{objVal})
diags.Append(d...)

return listVal, diags
}

func flattenStructures(ctx context.Context, apiObjects []*service.Structure) (types.List, diag.Diagnostics) {
var diags diag.Diagnostics
elemType := types.ObjectType{AttrTypes: structureAttrTypes}

if len(apiObjects) == 0 {
return types.ListNull(elemType), diags
}

elems := []attr.Value{}
for _, apiObject := range apiObjects {
if apiObject == nil {
continue
}

obj := map[string]attr.Value{
// ... nested attribute handling ...
}
objVal, d := types.ObjectValue(structureAttrTypes, obj)
diags.Append(d...)

elems = append(elems, objVal)
}

listVal, d := types.ListValue(elemType, elems)
diags.Append(d...)

return listVal, diags
}
</code></pre> <pre><code>func flattenStructure(apiObject *service.Structure) map[string]interface{} {
