func flattenStructures(apiObjects []*service.Structure) []interface{} {
if len(apiObjects) == 0 {
return nil
}

var tfList []interface{}

for _, apiObject := range apiObjects {
if apiObject == nil {
continue
}

tfList = append(tfList, flattenStructure(apiObject))
}

return tfList
}
